#include <bits/stdc++.h>
using namespace std;

/*
====================================================
GRAPH SEARCH & PATHFINDING ALGORITHMS (ALL-IN-ONE)
====================================================

Node numbering assumed: 0 to N-1

----------------------------------------------------
1. Breadth First Search (BFS)
Concept:
- Level by level traversal
- Uses Queue
- Finds shortest path in unweighted graph
----------------------------------------------------
*/
void BFS(int start, vector<vector<int>>& adj) {
    vector<bool> visited(adj.size(), false);
        queue<int> q;

            visited[start] = true;
                q.push(start);

                    while (!q.empty()) {
                            int u = q.front(); q.pop();
                                    cout << u << " ";

                                            for (int v : adj[u]) {
                                                        if (!visited[v]) {
                                                                        visited[v] = true;
                                                                                        q.push(v);
                                                                                                    }
                                                                                                            }
                                                                                                                }
                                                                                                                    cout << endl;
                                                                                                                    }

                                                                                                                    /*
                                                                                                                    ----------------------------------------------------
                                                                                                                    2. Depth First Search (DFS)
                                                                                                                    Concept:
                                                                                                                    - Depth-wise traversal
                                                                                                                    - Uses recursion or stack
                                                                                                                    - Useful for cycle detection, components
                                                                                                                    ----------------------------------------------------
                                                                                                                    */
                                                                                                                    void DFS(int u, vector<vector<int>>& adj, vector<bool>& visited) {
                                                                                                                        visited[u] = true;
                                                                                                                            cout << u << " ";

                                                                                                                                for (int v : adj[u]) {
                                                                                                                                        if (!visited[v])
                                                                                                                                                    DFS(v, adj, visited);
                                                                                                                                                        }
                                                                                                                                                        }

                                                                                                                                                        /*
                                                                                                                                                        ----------------------------------------------------
                                                                                                                                                        3. Depth Limited Search (DLS)
                                                                                                                                                        Concept:
                                                                                                                                                        - DFS with depth limit
                                                                                                                                                        - Prevents infinite depth
                                                                                                                                                        ----------------------------------------------------
                                                                                                                                                        */
                                                                                                                                                        bool DLS(int u, int target, int limit, vector<vector<int>>& adj) {
                                                                                                                                                            if (u == target) return true;
                                                                                                                                                                if (limit <= 0) return false;

                                                                                                                                                                    for (int v : adj[u]) {
                                                                                                                                                                            if (DLS(v, target, limit - 1, adj))
                                                                                                                                                                                        return true;
                                                                                                                                                                                            }
                                                                                                                                                                                                return false;
                                                                                                                                                                                                }

                                                                                                                                                                                                /*
                                                                                                                                                                                                ----------------------------------------------------
                                                                                                                                                                                                4. Iterative Deepening DFS (IDDFS)
                                                                                                                                                                                                Concept:
                                                                                                                                                                                                - Runs DLS with increasing depth
                                                                                                                                                                                                - Combines BFS completeness + DFS memory efficiency
                                                                                                                                                                                                ----------------------------------------------------
                                                                                                                                                                                                */
                                                                                                                                                                                                bool IDDFS(int start, int target, vector<vector<int>>& adj, int maxDepth) {
                                                                                                                                                                                                    for (int depth = 0; depth <= maxDepth; depth++) {
                                                                                                                                                                                                            if (DLS(start, target, depth, adj))
                                                                                                                                                                                                                        return true;
                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                return false;
                                                                                                                                                                                                                                }

                                                                                                                                                                                                                                /*
                                                                                                                                                                                                                                ----------------------------------------------------
                                                                                                                                                                                                                                5. Uniform Cost Search (UCS)
                                                                                                                                                                                                                                Concept:
                                                                                                                                                                                                                                - Expands node with lowest path cost
                                                                                                                                                                                                                                - Uses Priority Queue
                                                                                                                                                                                                                                - Optimal for weighted graph
                                                                                                                                                                                                                                ----------------------------------------------------
                                                                                                                                                                                                                                */
                                                                                                                                                                                                                                void UCS(int start, vector<vector<pair<int,int>>>& adj) {
                                                                                                                                                                                                                                    priority_queue<pair<int,int>, vector<pair<int,int>>, greater<>> pq;
                                                                                                                                                                                                                                        vector<int> dist(adj.size(), INT_MAX);

                                                                                                                                                                                                                                            pq.push({0, start});
                                                                                                                                                                                                                                                dist[start] = 0;

                                                                                                                                                                                                                                                    while (!pq.empty()) {
                                                                                                                                                                                                                                                            auto [cost, u] = pq.top(); pq.pop();

                                                                                                                                                                                                                                                                    for (auto [v, w] : adj[u]) {
                                                                                                                                                                                                                                                                                if (dist[v] > cost + w) {
                                                                                                                                                                                                                                                                                                dist[v] = cost + w;
                                                                                                                                                                                                                                                                                                                pq.push({dist[v], v});
                                                                                                                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                                                                                                        }

                                                                                                                                                                                                                                                                                                                                        /*
                                                                                                                                                                                                                                                                                                                                        ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                        6. Greedy Best First Search (GBFS)
                                                                                                                                                                                                                                                                                                                                        Concept:
                                                                                                                                                                                                                                                                                                                                        - Chooses node with lowest heuristic value
                                                                                                                                                                                                                                                                                                                                        - Fast but NOT optimal
                                                                                                                                                                                                                                                                                                                                        ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                        */
                                                                                                                                                                                                                                                                                                                                        struct GBFSNode {
                                                                                                                                                                                                                                                                                                                                            int node, h;
                                                                                                                                                                                                                                                                                                                                                bool operator>(const GBFSNode& other) const {
                                                                                                                                                                                                                                                                                                                                                        return h > other.h;
                                                                                                                                                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                                                                                                                                            };

                                                                                                                                                                                                                                                                                                                                                            void GBFS(int start, vector<vector<int>>& adj, vector<int>& heuristic) {
                                                                                                                                                                                                                                                                                                                                                                priority_queue<GBFSNode, vector<GBFSNode>, greater<>> pq;
                                                                                                                                                                                                                                                                                                                                                                    vector<bool> visited(adj.size(), false);

                                                                                                                                                                                                                                                                                                                                                                        pq.push({start, heuristic[start]});

                                                                                                                                                                                                                                                                                                                                                                            while (!pq.empty()) {
                                                                                                                                                                                                                                                                                                                                                                                    auto cur = pq.top(); pq.pop();
                                                                                                                                                                                                                                                                                                                                                                                            int u = cur.node;

                                                                                                                                                                                                                                                                                                                                                                                                    if (visited[u]) continue;
                                                                                                                                                                                                                                                                                                                                                                                                            visited[u] = true;
                                                                                                                                                                                                                                                                                                                                                                                                                    cout << u << " ";

                                                                                                                                                                                                                                                                                                                                                                                                                            for (int v : adj[u]) {
                                                                                                                                                                                                                                                                                                                                                                                                                                        if (!visited[v])
                                                                                                                                                                                                                                                                                                                                                                                                                                                        pq.push({v, heuristic[v]});
                                                                                                                                                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        cout << endl;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                        /*
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        7. A* Search Algorithm
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        Concept:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        - f(n) = g(n) + h(n)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        - Best heuristic pathfinding algorithm
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        - Optimal if heuristic is admissible
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        */
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        struct AStarNode {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                            int node, g, f;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                bool operator>(const AStarNode& other) const {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        return f > other.f;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            };

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            void AStar(int start, int goal,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       vector<vector<pair<int,int>>>& adj,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  vector<int>& h) {

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      priority_queue<AStarNode, vector<AStarNode>, greater<>> pq;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          vector<int> gCost(adj.size(), INT_MAX);

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              pq.push({start, 0, h[start]});
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  gCost[start] = 0;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      while (!pq.empty()) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              auto cur = pq.top(); pq.pop();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      int u = cur.node;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              if (u == goal) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          cout << "Goal Reached\n";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      return;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      for (auto [v, w] : adj[u]) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  int newG = gCost[u] + w;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              if (newG < gCost[v]) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              gCost[v] = newG;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              pq.push({v, newG, newG + h[v]});
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      /*
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      8. Hill Climbing Algorithm
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      Concept:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      - Chooses best neighbor only
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      - Can get stuck in local maxima
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      */
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      int HillClimbing(int start, vector<vector<int>>& adj, vector<int>& h) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          int current = start;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              while (true) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      int best = current;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              for (int v : adj[current]) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          if (h[v] < h[best])
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          best = v;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          if (best == current)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      return current;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              current = best;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  /*
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  9. Dijkstra Algorithm
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  Concept:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  - Shortest path for non-negative weights
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  - Greedy + Priority Queue
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  */
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  void Dijkstra(int src, vector<vector<pair<int,int>>>& adj) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      priority_queue<pair<int,int>, vector<pair<int,int>>, greater<>> pq;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          vector<int> dist(adj.size(), INT_MAX);

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              pq.push({0, src});
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  dist[src] = 0;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      while (!pq.empty()) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              auto [d, u] = pq.top(); pq.pop();

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      for (auto [v, w] : adj[u]) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  if (dist[v] > d + w) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  dist[v] = d + w;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  pq.push({dist[v], v});
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          /*
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          10. Bellman-Ford Algorithm
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          Concept:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          - Handles negative edge weights
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          - Detects negative cycles
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          */
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          void BellmanFord(int V, vector<tuple<int,int,int>>& edges, int src) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              vector<int> dist(V, INT_MAX);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  dist[src] = 0;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      for (int i = 1; i < V; i++) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              for (auto [u, v, w] : edges) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          if (dist[u] != INT_MAX && dist[u] + w < dist[v])
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          dist[v] = dist[u] + w;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      /*
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      11. Floyd Warshall Algorithm
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      Concept:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      - All-pairs shortest path
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      - Dynamic Programming
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      */
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      void FloydWarshall(vector<vector<int>>& dist) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          int V = dist.size();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              for (int k = 0; k < V; k++)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      for (int i = 0; i < V; i++)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  for (int j = 0; j < V; j++)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  if (dist[i][k] + dist[k][j] < dist[i][j])
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      dist[i][j] = dist[i][k] + dist[k][j];
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      /*
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      MAIN FUNCTION (Example)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ----------------------------------------------------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      */
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      int main() {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          cout << "All Graph Algorithms Loaded Successfully!\n";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              return 0;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              }